import { ReactNode, useState } from "react"


export default function useForm() {
    const [ value, setValue ] = useState()

    const onChange = ( {target}: ReactNode ) => {
        setValue(target.value)
    }

    return {
        value, 
        setValue,
        onChange
    }
}
