import { createContext, ReactNode, useEffect, useState } from "react";

import { useHistory } from 'react-router-dom'
// import useNa

import { GET_USER, TOKEN_POST, TOKEN_VALIDATE_POST } from "../utils/api";

type UserStorageProps = {
  children: ReactNode;
};

type UserLoginType = {
  username: string;
  password: string;
};

type User = {
  id: number;
  email: string;
  nome: string;
  username: string;
};

export const UserContext = createContext({} as any);


export default function UserStorage({ children }: UserStorageProps) {
  const [data, setData] = useState<User>(null as any);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  cnst 

  useEffect(() => {
    const autoLogin = async () => {
      const token = localStorage.getItem("token");
      if (token) {
        try {
          setLoading(true);
          const { url, options } = TOKEN_VALIDATE_POST(token);
          const response = await fetch(url, options);
          if (!response.ok) throw new Error("Token inválido");
          await getUser(token);
        } catch (error) {
        } finally {
          setLoading(false);
        }
      }
    };

    autoLogin();
  }, []);

  const getUser = async (token: string) => {
    const { url, options } = GET_USER(token);
    const response = await fetch(url, options);
    const json = await response.json();
    setData(json);

  };

  const userLogin = async (user: UserLoginType) => {
    const { url, options } = TOKEN_POST(user);

    try {
      setLoading(true);
      const response = await fetch(url, options);
      if (!response.ok) throw new Error(`Error: ${response.statusText}`);
      const { token } = await response.json();
      localStorage.setItem("token", token);
      getUser(token);
    } catch (error: any) {
      setError(error.message);
      setLoading(false);
    } finally {
      setLoading(false);
    }
  };

  return (
    <UserContext.Provider value={{ userLogin, data, error, loading }}>
      {children}
    </UserContext.Provider>
  );
}
